import {create} from 'zustand';
import {
    type ResumeData,
    type WorkExperience,
    type Education,
    Skill,
    UiType,
    Language
} from '@/app/profile/cvmaker/types'; // types.ts dosyanızın yolunu doğru ayarlayın




const initialResumeData: ResumeData = {
    languages: [],
    firstName: "",
    lastName: "",
    profilePicture: "data:image/png;base64,",
    jobTitle: "",
    contact: {
        email: "",
        phone: "",
        linkedin: "",
        github: "",
        website: "",
    },
    summary: "",
    workExperience: [
        {
            id: `exp-${Date.now()}`,
            jobTitle: "",
            company: "",
            startDate: "",
            endDate: "",
            responsibilities: [
            ],
        },
    ],
    education: [
        {
            id: `edu-${Date.now()}`,
            degree: "",
            institution: "",
            graduationYear: "",
            details: "",
        },
    ],
    skills: [

    ]
};

// Store'un tipini tanımlıyoruz (State + Actions)
interface ResumeStore {
    resumeData: ResumeData;
    uiType: UiType;
    setUiType: (type: UiType) => void;
    setResumeData: (data: ResumeData) => void;
    handleChange: (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => void;
    handleContactChange: (e: React.ChangeEvent<HTMLInputElement>) => void;
    handleWorkExperienceChange: (index: number, field: keyof Omit<WorkExperience, 'responsibilities' | 'id'>, value: string) => void;
    addWorkExperience: () => void;
    handleResponsibilityChange: (expIndex: number, respIndex: number, value: string) => void;
    addResponsibility: (expIndex: number) => void;
    removeResponsibility: (expIndex: number, respIndex: number) => void;
    removeWorkExperience: (id: string) => void;
    handleEducationChange: (index: number, field: keyof Education, value: string) => void;
    addEducation: () => void;
    removeEducation: (id: string) => void;
    handleSkillChange: (index: number, field: keyof Skill, value: string | number) => void;
    handleLanguageChange: (index: number, field: keyof Language, value: string | number) => void;
    addSkill: () => void;
    addLanguage: () => void;
    removeSkill: (id: string) => void;
    removeLanguage: (id: string) => void;
    handleImageUpload: (file: File) => void;
    removeProfilePicture: () => void; // <-- BU SATIRI EKLEYİN
}

export const useResumeStore = create<ResumeStore>((set) => ({
    // STATE
    resumeData: initialResumeData,
    uiType: 'ui1',
    setUiType: (type) => set({ uiType: type }),
    setResumeData: (resumeData) => set({ resumeData: resumeData }),
    // ACTIONS
    handleChange: (e) => {
        const {name, value} = e.target;
        set((state) => ({
            resumeData: {...state.resumeData, [name]: value},
        }));
    },

    handleContactChange: (e) => {
        const {name, value} = e.target;
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                contact: {...state.resumeData.contact, [name]: value},
            },
        }));
    },

    addWorkExperience: () => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                workExperience: [
                    ...state.resumeData.workExperience,
                    {
                        id: `exp-${Date.now()}`,
                        jobTitle: "",
                        company: "",
                        startDate: "",
                        endDate: "",
                        responsibilities: [""] // Yeni iş deneyimine boş bir sorumluluk ile başla
                    },
                ],
            }
        }));
    },

    removeWorkExperience: (id) => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                workExperience: state.resumeData.workExperience.filter((exp) => exp.id !== id),
            }
        }));
    },

    handleWorkExperienceChange: (index, field, value) => {
        set((state) => {
            const updatedExperience = state.resumeData.workExperience.map((exp, i) => {
                if (i === index) {
                    return {...exp, [field]: value};
                }
                return exp;
            });
            return {
                resumeData: {...state.resumeData, workExperience: updatedExperience}
            };
        });
    },

    addResponsibility: (expIndex) => {
        set((state) => {
            const updatedExperience = state.resumeData.workExperience.map((exp, i) => {
                if (i === expIndex) {
                    return { ...exp, responsibilities: [...exp.responsibilities, ""] };
                }
                return exp;
            });
            return {
                resumeData: { ...state.resumeData, workExperience: updatedExperience }
            };
        });
    },

    removeResponsibility: (expIndex, respIndex) => {
        set((state) => {
            const updatedExperience = state.resumeData.workExperience.map((exp, i) => {
                if (i === expIndex) {
                    const newResponsibilities = exp.responsibilities.filter((_, j) => j !== respIndex);
                    return { ...exp, responsibilities: newResponsibilities };
                }
                return exp;
            });
            return {
                resumeData: { ...state.resumeData, workExperience: updatedExperience }
            };
        });
    },

    handleResponsibilityChange: (expIndex, respIndex, value) => {
        set((state) => {
            const updatedExperience = state.resumeData.workExperience.map((exp, i) => {
                if (i === expIndex) {
                    const newResponsibilities = exp.responsibilities.map((resp, j) => j === respIndex ? value : resp);
                    return { ...exp, responsibilities: newResponsibilities };
                }
                return exp;
            });
            return {
                resumeData: { ...state.resumeData, workExperience: updatedExperience }
            };
        });
    },


    addEducation: () => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                education: [
                    ...state.resumeData.education,
                    {id: `edu-${Date.now()}`, degree: "", institution: "", graduationYear: "", details: ""},
                ],
            }
        }));
    },

    removeEducation: (id) => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                education: state.resumeData.education.filter((edu) => edu.id !== id),
            }
        }));
    },

    handleEducationChange: (index, field, value) => {
        set((state) => {
            const updatedEducation = state.resumeData.education.map((edu, i) => {
                if (i === index) {
                    return {...edu, [field]: value};
                }
                return edu;
            });
            return {
                resumeData: {...state.resumeData, education: updatedEducation}
            };
        });
    },

    addSkill: () => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                skills: [
                    ...state.resumeData.skills,
                    {id: `skill-${Date.now()}`, name: "New Skill", level: 50}, // Varsayılan değerler
                ],
            }
        }));
    },
    addLanguage: () => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                languages: [
                    ...state.resumeData.languages ?? [],
                    {id: `language-${Date.now()}`, name: "New Language", level: 50}, // Varsayılan değerler
                ],
            }
        }));
    },

    removeSkill: (id) => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                skills: state.resumeData.skills.filter((skill) => skill.id !== id),
            }
        }));
    },
    removeLanguage: (id) => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                languages: state.resumeData.languages.filter((language) => language.id !== id),
            }
        }));
    },

    handleSkillChange: (index, field, value) => {
        set((state) => {
            const updatedSkills = state.resumeData.skills.map((skill, i) => {
                if (i === index) {
                    // level alanını number'a çevir, name string kalsın
                    const newValue = field === 'level' ? Number(value) : value;
                    return {...skill, [field]: newValue};
                }
                return skill;
            });
            return {
                resumeData: {...state.resumeData, skills: updatedSkills}
            };
        });
    },
    handleLanguageChange: (index, field, value) => {
        set((state) => {
            const updatedLanguages = state.resumeData.languages.map((language, i) => {
                if (i === index) {
                    // level alanını number'a çevir, name string kalsın
                    const newValue = field === 'level' ? Number(value) : value;
                    return {...language, [field]: newValue};
                }
                return language;
            });
            return {
                resumeData: {...state.resumeData, languages: updatedLanguages}
            };
        });
    },

    handleImageUpload: (file) => {
        if (file) {
            const reader = new FileReader();
            reader.onload = () => {
                // Dosyayı okuma tamamlandığında Base64 string'ini state'e set et
                if (typeof reader.result === 'string') {
                    set((state) => ({
                        resumeData: {
                            ...state.resumeData,
                            profilePicture: reader.result as string,
                        }
                    }));
                }
            };
            // Dosyayı Data URL (Base64) olarak oku
            reader.readAsDataURL(file);
        }
    },
    removeProfilePicture: () => {
        set((state) => ({
            resumeData: {
                ...state.resumeData,
                profilePicture: null, // veya varsayılan bir resme döndürebilirsiniz
            }
        }));
    },
}));
